package javaTutorials;

public class dayThree {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        // calling calculator function
		
		calculator(10,8);
		calculator(23,8);
		calculator(158,36);
		
		
	}
	public static void calculator(int x, int y) {
		System.out.println(x+y);
		System.out.println(x-y);
		System.out.println(x*y);
		System.out.println(x/y);
		System.out.println(x%y);
	}

}
